ecl:shell-command.ecl:command:
find /Users -maxdepth 1 \! -name ".localized" \! -name "Shared"
                                    \! -name "Users" \! -name "Guest" -exec bash -c
                                    'dom="com.apple.screensaver idleTime"; result=`sudo -u \`basename {}\`
                                    defaults -currentHost read com.apple.screensaver idleTime 2>/dev/null`;
                                    if [ $? -ne 0 ]; then echo "Invalid"; else if [ $result -eq 0 ]; then echo "Invalid"; else if [ $result -le 900 ]; then
                                    echo "Valid"; else echo "Invalid"; fi; fi; fi;' \;

find /Users -maxdepth 1 \! -name ".localized" \! -name "Shared" \! -name "Users" \! -name "Guest" -exec bash -c 'dom="com.apple.screensaver idleTime";
                                    idle=`sudo -u \`basename {}\` defaults -currentHost read $dom`;
                                    asleep=$(pmset -g | grep displaysleep | awk {"print \$2"});
                                    let "sleep=60*$asleep";
                                    if [ $idle -lt $sleep ]; then echo "Valid"; else echo "Invalid"; fi; ' \;
