netblocks='<system-or-network> <system-or-network> ...'awk
                        < /etc/inetd.conf ' /^[ ]*(#|$)/ { next } /^ / { next } /^rpc[ ]/ {
                        services[$9]=1; next } { services[$1]=1; next } END { for(service in
                        services) { print service " allow '"$netblocks"'" print service " deny" } }
                        ' >> /var/adm/inetd.sec
